package 데이터;

public class 기본데이터2 {

	public static void main(String[] args) { // [] : 대괄호
		// TODO Auto-generated method stub
		//기본 데이터 4가지 : 정수, 실수, 문자, 논리 : 컴퓨터가 인식할수 있게 저장 : int var
		//기호 => 연산자 ,ram(memory:임시기억)에 저장
		 int age = 100; // 정수 
		 double height = 122.2; // 실수
		 char gender = '남'; //1글자 // '문자' '한글자 : 싱글'
		 boolean food = true; //false // 논리 
		 System.out.println("오늘은 월요일입니다."); //cpu처리 : println(출력할것);
		 System.out.println("내 나이는:" + age); //결합연산자 : 기호
		 //글자들: 문자+문자+문자...(여러글자, 문자열, String, 실)
	}

}
