package 복습;

public class 기본형데이터 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 정수, 실수, 문자, 논리 //자바 : 자바스크립스 : 제이쿼리
		int count = 20;
		double 비율 = 0.25;
		char 층 = '7';
		boolean hot = true;
		System.out.println("총 인원은" + count + "원"); // 복사 : ctrl + alt + 방향키 // "" : string
		System.out.println("총 비율은" + 비율); // 변수 : 갈색
		System.out.println("층수는" + 층 + "층");// + : 결합기호
		System.out.println("더운가요" + hot);
	}

}
